import React, { useState } from 'react';
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  VStack,
  Select,
  useToast,
  Card,
  CardBody,
  Heading,
  Text,
  List,
  ListItem,
  ListIcon,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
} from '@chakra-ui/react';
import { MdCheckCircle, MdWarning } from 'react-icons/md';

const FirstAidGuide = () => {
  const [emergency, setEmergency] = useState({
    condition: '',
    location: '',
    severity: 'moderate',
  });
  const [instructions, setInstructions] = useState(null);
  const toast = useToast();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/emergency/firstaid', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(emergency),
      });

      const data = await response.json();
      setInstructions(data);

      toast({
        title: 'Success',
        description: 'First aid instructions generated successfully.',
        status: 'success',
        duration: 5000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to get instructions. Please try again.',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  return (
    <Box p={4}>
      <Alert status="warning" mb={6}>
        <AlertIcon />
        <Box>
          <AlertTitle>Emergency Warning</AlertTitle>
          <AlertDescription>
            If this is a life-threatening emergency, immediately call emergency services (102) before proceeding.
          </AlertDescription>
        </Box>
      </Alert>

      <VStack spacing={6} align="stretch">
        <Card>
          <CardBody>
            <Heading size="md" mb={4}>Emergency Details</Heading>
            <FormControl>
              <FormLabel>Condition/Injury</FormLabel>
              <Input
                value={emergency.condition}
                onChange={(e) => setEmergency({ ...emergency, condition: e.target.value })}
                placeholder="e.g., cut, burn, fracture"
              />
            </FormControl>

            <FormControl mt={4}>
              <FormLabel>Severity</FormLabel>
              <Select
                value={emergency.severity}
                onChange={(e) => setEmergency({ ...emergency, severity: e.target.value })}
              >
                <option value="mild">Mild</option>
                <option value="moderate">Moderate</option>
                <option value="severe">Severe</option>
              </Select>
            </FormControl>

            <FormControl mt={4}>
              <FormLabel>Location</FormLabel>
              <Input
                value={emergency.location}
                onChange={(e) => setEmergency({ ...emergency, location: e.target.value })}
                placeholder="Your current location"
              />
            </FormControl>

            <Button mt={4} colorScheme="red" onClick={handleSubmit}>
              Get First Aid Instructions
            </Button>
          </CardBody>
        </Card>

        {instructions && (
          <Card>
            <CardBody>
              <Heading size="md" mb={4}>First Aid Instructions</Heading>

              <Text fontWeight="bold" mb={2}>Follow these steps carefully:</Text>
              <List spacing={3}>
                {instructions.instructions.map((instruction, index) => (
                  <ListItem key={index}>
                    <ListIcon as={MdCheckCircle} color="green.500" />
                    {instruction}
                  </ListItem>
                ))}
              </List>

              <Text fontWeight="bold" mt={4} mb={2}>Emergency Contacts:</Text>
              <List spacing={3}>
                {instructions.emergency_contacts.map((contact, index) => (
                  <ListItem key={index}>
                    <ListIcon as={MdWarning} color="red.500" />
                    {contact.name}: {contact.phone}
                  </ListItem>
                ))}
              </List>

              <Alert status="info" mt={4}>
                <AlertIcon />
                Remember to stay calm and follow these instructions while waiting for professional help.
              </Alert>
            </CardBody>
          </Card>
        )}
      </VStack>
    </Box>
  );
};

export default FirstAidGuide;
